INSERT INTO `content` (`content_id`, `content_title`, `title_plain`, `content_detail`, `content_summary`, `created_date`, `viewed`, `status`, `seo_id`, `locked`) VALUES
(1, 'Giới thiệu chung về công ty', NULL, '\r\n<p><strong><em>Otoduc.vn</em></strong></p>\r\n<p><strong><em>THƯ NGỎ</em></strong></p>\r\n<p><strong><em><br /></em></strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ch&agrave;o Q&uacute;y Kh&aacute;ch H&agrave;ng. C&ocirc;ng ty Euro Auto, nh&agrave; nhập khẩu xe BMW ch&iacute;nh h&atilde;ng duy nhất tại Việt Nam, tr&acirc;n trọng cảm ơn v&igrave; sự y&ecirc;u mến v&agrave; ủng hộ của Q&uacute;y Kh&aacute;ch H&agrave;ng đối với sản phẩm xe BMW của ch&uacute;ng t&ocirc;i trong suốt thời gian qua. Để chi &acirc;n kh&aacute;ch h&agrave;ng, đ&aacute;p lại sự mến mộ m&agrave; Q&uacute;y Kh&aacute;ch H&agrave;ng d&agrave;nh cho, ch&uacute;ng t&ocirc;i li&ecirc;n tục đổi mới: Nhập về Việt Nam những sản phẩm c&oacute; c&ocirc;ng nghệ mới nhất, model nhất v&agrave; đặc biệt l&agrave; ph&ugrave; hợp với điều kiện sử dụng ở Việt Nam nhất. B&ecirc;n cạnh đ&oacute; ch&uacute;ng t&ocirc;i rất ch&uacute; trọng đến kh&acirc;u chăm s&oacute;c kh&aacute;ch h&agrave;ng như: li&ecirc;n tục tổ chức c&aacute;c lớp đ&agrave;o tạo kỹ năng b&aacute;n h&agrave;ng cao cấp, cử nh&acirc;n vi&ecirc;n kỹ thuật đi tu nghiệp tại Đức v&agrave; c&aacute;c nước c&oacute; nền c&ocirc;ng nghiệp &ocirc; t&ocirc; ph&aacute;t triển d&agrave;i hạn, dịch vụ đỗ xe miễn ph&iacute; tại khu vực đ&ocirc;ng d&acirc;n cư, v&agrave; rất nhiều c&aacute;c chương tr&igrave;nh khuyến mại hỗ trợ về gi&aacute;, bốc thăm tr&uacute;ng thưởng... Hiện ch&uacute;ng t&ocirc;i đang c&oacute; c&aacute;c d&ograve;ng xe với tr&ecirc;n 10 m&agrave;u lựa trọn cho mỗi d&ograve;ng, sẵn s&agrave;ng giao xe ngay cho qu&yacute; kh&aacute;ch như: BMW 320i, 325i ,&nbsp;523i, 528i&nbsp;, 730Li, 740Li, 750Li ,760Li , Z4 23i, X1 18i, X1 28i , X3 28i, X5 35i,&nbsp;X6 35i. Cảm ơn Q&uacute;y Kh&aacute;ch H&agrave;ng</p>\r\n<ul>\r\n<li><strong>Xe mới 100%, nhập khẩu nguy&ecirc;n chiếc từ Đức.</strong><strong>&nbsp;</strong></li>\r\n<li><strong>Series xe mới nhất .</strong></li>\r\n<li><strong>Thiết kế v&agrave; động cơ ph&ugrave; hợp kh&iacute; hậu v&agrave; điều kiện cung đường tại Việt Nam<br /></strong></li>\r\n<li><strong>Lựa&nbsp; chọn phụ kiện theo y&ecirc;u cầu .</strong></li>\r\n<li><strong>Bảo h&agrave;nh 2 năm kh&ocirc;ng giới hạn km ,</strong> \r\n<ul>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Q&uacute;y Kh&aacute;ch H&agrave;ng quan t&acirc;m đến sản phẩm BMW v&agrave; đăng k&yacute; l&aacute;i thử c&aacute;c d&ograve;ng xe sang trọng BMW, xin vui l&ograve;ng li&ecirc;n hệ với ch&uacute;ng t&ocirc;i. Ch&uacute;ng t&ocirc;i xin được phục vụ Q&uacute;y Kh&aacute;ch H&agrave;ng suốt 24h trong ng&agrave;y.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Tư Vấn B&aacute;n H&agrave;ng: Phạm Mạnh Cường<br /></strong></p>\r\n<p>Tel: <strong>&nbsp;0902 178 229, 094 626 0000</strong></p>\r\n<p><strong>Email: cuong.pham@bmw.vn</strong></p>\r\n<p><strong>Website: www.otoduc.vn<br /></strong></p>\r\n', '', '2011-11-13 16:09:50', NULL, NULL, NULL, 0),
(20, 'Thủ tục mua xe dành cho cá nhân', NULL, '\r\n<p style="text-align: left;"><strong>﻿﻿﻿</strong></p>\r\n<p style="text-align: center;"><span id="ctl00_cntRightNavBar_NewsContent1"><span style="font-family: Arial; font-size: x-small;"><span style="font-family: Arial; font-size: 13.5pt;">HƯỚNG DẪN THỦ TỤC MUA XE&nbsp;BMW TRẢ G&Oacute;P</span></span></span></p>\r\n<p style="text-align: center;"><span style="color: #ff0000;"><strong>C&Aacute; NH&Acirc;N MUA </strong></span></p>\r\n<p>Sao y hộ khẩu tại TP H&agrave; Nội.<br />Sao y CMND.<br />Giấy chứng nhận độc th&acirc;n hoặc giấy kết h&ocirc;n.<br />Chứng minh thu nhập bằng: hợp đồng lao động, bảng lương, sổ tiết kiệm, t&agrave;i khoản c&aacute; nh&acirc;n.<br />C&aacute; nh&acirc;n sở hữu t&agrave;i sản c&oacute; gi&aacute; trị: nh&agrave; cửa, đất đai, c&aacute;c loại &ocirc; t&ocirc; , m&aacute;y m&oacute;c, d&acirc;y chuyền nh&agrave; m&aacute;y, nh&agrave; xưởng,..</p>\r\n<p>Hợp đồng cho thu&ecirc; xe, thu&ecirc; nh&agrave;, cho thu&ecirc; xưởng, giấy g&oacute;p vốn, cổ phần, cổ phiếu, tr&aacute;i phiếu.</p>\r\n<p>Nếu  c&aacute; nh&acirc;n c&oacute; c&ocirc;ng ty ri&ecirc;ng m&agrave; thu nhập chủ yếu từ c&ocirc;ng ty th&igrave; th&ecirc;m: b&aacute;o  c&aacute;o thuế, b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh, bảng lương, bảng chia lợi nhuận từ c&ocirc;ng ty.  Giấy ph&eacute;p đăng k&yacute; kinh doanh.</p>\r\n<p>Ho&aacute; đơn chi ph&iacute; c&aacute; nh&acirc;n c&aacute;c th&aacute;ng gần nhất: điện thoại, chi ph&iacute; giao dịch l&agrave;m ăn.</p>\r\n<p>Trong  trường hợp c&aacute; nh&acirc;n mua kh&ocirc;ng đủ điều kiện, c&oacute; thể nhờ người th&acirc;n c&oacute; khả  năng thu nhập tốt l&agrave;m giấy bảo l&atilde;nh cho Ng&acirc;n h&agrave;ng thẩm định.<br />Đơn xin vay vốn Ng&acirc;n h&agrave;ng v&agrave; phương &aacute;n trả l&atilde;i ( theo mẫu của Ng&acirc;n h&agrave;ng ).</p>\r\n<p style="text-align: center;"><span style="color: #ff0000;"><strong>NG&Acirc;N H&Agrave;NG HOẶC C&Ocirc;NG TY CHO THU&Ecirc; T&Agrave;I CH&Iacute;NH SẼ T&Agrave;I TRỢ</strong></span></p>\r\n<p>Ng&acirc;n h&agrave;ng<br />Mức t&agrave;i trợ th&ocirc;ng thường 75% tr&ecirc;n t&agrave;i sản thế chấp.<br />Thời gian tối đa 5 năm.<br />L&atilde;i suất th&ocirc;ng thường của NHTMCP : 1,0625%/th&aacute;ng.<br />Thời gian thẩm định hồ sơ sau khi nhận đầy đủ 24 giờ.<br />Chi ph&iacute; : ph&iacute; thủ tục h&agrave;nh ch&aacute;nh .<br />Thủ  tục h&agrave;nh ch&aacute;nh tại ng&acirc;n h&agrave;ng gồm: ph&iacute; đảm bảo t&agrave;i sản, ph&iacute; mở t&agrave;i  khoản, ph&iacute; c&ocirc;ng chứng sao y, c&agrave; vẹt xe, mua bảo hiểm th&acirc;n xe 1,5% trị  gi&aacute; xe trong thời gian vay.<br />C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh<br />Mức t&agrave;i trợ tối đa 75% tr&ecirc;n t&agrave;i sản thế chấp.<br />Thời gian tối đa 05 năm.<br />L&atilde;i suất ưu đ&atilde;i : 1% - 1,1% th&aacute;ng.<br />Thời gian thẩm định hồ sơ sau khi nhận đầy đủ: 05 ng&agrave;y.<br />C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh đứng t&ecirc;n chủ xe, sau khi kh&aacute;ch trả hết nợ, c&ocirc;ng ty sẽ sang t&ecirc;n lại cho kh&aacute;ch.<br />Chi ph&iacute; : ph&iacute; thủ tục h&agrave;nh ch&iacute;nh<br />Thủ  tục h&agrave;nh ch&iacute;nh tại Ng&acirc;n h&agrave;ng gồm: ph&iacute; đảm bảo t&agrave;i sản, ph&iacute; mở t&agrave;i  khỏan, ph&iacute; c&ocirc;ng chứng sao y, c&agrave; vẹt xe, mua bảo hiểm th&acirc;n xe trong thời  gian vay.<br />Đơn xin vay vốn ng&acirc;n h&agrave;ng v&agrave; phương &aacute;n trả l&atilde;i (theo mẫu của c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh)</p>\r\n<p style="margin: 3.75pt 3.75pt 5pt 3pt; text-align: center;"><strong><span style="font-family: Arial; color: #f01b24; font-size: 10pt;">QUY TR&Igrave;NH MUA XE TRẢ G&Oacute;P</span></strong><span style="font-family: Arial;"> </span></p>\r\n<p><span style="font-family: Arial; font-size: 10pt;">Kh&aacute;ch h&agrave;ng chuẩn bị đầy đủ hồ sơ theo hướng dẫn của nh&acirc;n vi&ecirc;n t&iacute;n dụng.<br />Nh&acirc;n vi&ecirc;n thẩm định đến tận nh&agrave; để thẩm định v&agrave; lấy hồ sơ.<br />Sau khi c&oacute; giấy t&agrave;i trợ t&iacute;n dụng v&agrave; hồ sơ xe.<br />Kh&aacute;ch h&agrave;ng phải tiến h&agrave;nh đ&oacute;ng tiền xe v&agrave; chi ph&iacute; l&agrave;m thủ tục đăng k&yacute; xe.<br />Khi  c&oacute; biển số xe v&agrave; c&oacute; giấy hẹn, kh&aacute;ch h&agrave;ng l&ecirc;n ng&acirc;n h&agrave;ng k&yacute; hợp đồng t&iacute;n  dụng, đ&oacute;ng ph&iacute; h&agrave;nh ch&iacute;nh v&agrave; giấy nhận nợ của ng&acirc;n h&agrave;ng.<br />Khi  tiền chuyển khoản của ng&acirc;n h&agrave;ng v&agrave;o t&agrave;i khoản của BMW, th&igrave; kh&aacute;ch h&agrave;ng  mang theo CMND v&agrave; giấy giới thiệu l&ecirc;n nhận xe, k&yacute; bi&ecirc;n bản b&agrave;n giao xe  với giấy tờ xe hợp lệ theo ph&aacute;p luật.<br />Khi c&oacute; giấy đăng k&yacute; xe, Ng&acirc;n h&agrave;ng sẽ đi đăng k&yacute; v&agrave; sao y cho kh&aacute;ch h&agrave;ng một bản để sử dụng.<br />Trường  hợp kh&aacute;ch mua qua C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh th&igrave; kh&aacute;ch h&agrave;ng đ&oacute;ng tiền  xe tại C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh. Chi ph&iacute; đăng k&yacute; xe kh&aacute;ch h&agrave;ng phải  chịu, Bi&ecirc;n bản b&agrave;n giao xe 03 b&ecirc;n c&ugrave;ng k&yacute; để c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh  giải ng&acirc;n cho h&atilde;ng xe.<br />Trường  hợp kh&aacute;ch h&agrave;ng ở tỉnh mua xe trả g&oacute;p th&igrave; chi ph&iacute; Dịch vụ đi đăng k&yacute; xe  do kh&aacute;ch h&agrave;ng chịu, t&ugrave;y theo tỉnh xa hay gần m&agrave; thỏa thuận.</span></p>\r\n<table border="0" cellspacing="0" cellpadding="0" width="100%">\r\n<tbody>\r\n<tr>\r\n<td style="width: 95%;"><span id="dnn_ctr525_dnnTITLE_lblTitle" class="Text3">C<span style="font-size: small;"><strong>&ocirc;ng ty v&agrave; tổ chức</strong></span></span></td>\r\n<td><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n&nbsp;\r\n<p style="margin: 3.75pt 3.75pt 12pt 3pt;"><strong><span style="color: #0000ff;"><span style="font-family: Arial; font-size: 10pt;"><span id="ctl00_cntRightNavBar_NewsContent1" style="color: #274c88;"><span style="font-size: small;">Thủ tục mua xe d&agrave;nh cho c&ocirc;ng ty v&agrave; tổ chức </span></span><br /></span></span></strong><span style="font-family: Arial; font-size: 10pt;"><br />Bằng việc li&ecirc;n kết với nhiều ng&acirc;n h&agrave;ng lớn,&nbsp;BMW lu&ocirc;n sẵn s&agrave;ng hỗ trợ kh&aacute;ch h&agrave;ng về vốn khi mua xe th&ocirc;ng qua h&igrave;nh thức trả g&oacute;p<span style="font-size: medium;"><strong>.</strong></span></span></p>\r\n<p style="text-align: center;"><span style="font-size: medium;"><strong>HƯỚNG DẪN THỦ TỤC MUA XE&nbsp;BMW TRẢ G&Oacute;P</strong></span></p>\r\n<p style="text-align: center;"><span style="font-family: Times New Roman; color: #ff0000;">&nbsp;</span></p>\r\n<p style="text-align: center;"><span style="color: #ff0000;"><strong>C&Ocirc;NG TY HOẶC DOANH NGHIỆP MUA XE</strong></span></p>\r\n<p>Giấy ph&eacute;p kinh doanh.<br />Giấy bổ nhiệm Gi&aacute;m đốc, bổ nhiệm kế to&aacute;n trưởng.<br />Giấy đăng k&yacute; sử dụng mẫu dấu (bản copy)<br />M&atilde; số thuế<br />B&aacute;o c&aacute;o thuế 01 năm gần nhất.<br />B&aacute;o c&aacute;o ho&aacute; đơn VAT 01 năm gần nhất.<br />B&aacute;o c&aacute;o t&agrave;i ch&iacute;nh 01 năm gần nhất.<br />Điều lệ c&ocirc;ng ty.<br />Bi&ecirc;n bản họp của hội đồng quản trị.<br />Hợp đồng kinh tế đầu ra, đầu v&agrave;o.<br />Giấy sở hữu cơ sở vật chất: nh&agrave; m&aacute;y, d&acirc;y chuyền, m&aacute;y m&oacute;c, thiết bị, nh&agrave; xưởng, &ocirc;t&ocirc; kh&aacute;c<br />Đơn xin vay vốn ng&acirc;n h&agrave;ng v&agrave; phương &aacute;n trả l&atilde;i ( theo mẫu của Ng&acirc;n h&agrave;ng ).</p>\r\n<p style="text-align: center;"><span style="color: #ff0000;"><strong>NG&Acirc;N H&Agrave;NG HOẶC C&Ocirc;NG TY CHO THU&Ecirc; T&Agrave;I CH&Iacute;NH SẼ T&Agrave;I TRỢ</strong></span></p>\r\n<p>Ng&acirc;n h&agrave;ng<br />Mức t&agrave;i trợ th&ocirc;ng thường 75% tr&ecirc;n t&agrave;i sản thế chấp.<br />Thời gian tối đa 5 năm.<br />L&atilde;i suất th&ocirc;ng thường của NHTMCP : 1,0625%/th&aacute;ng.<br />Thời gian thẩm định hồ sơ sau khi nhận đầy đủ 24 giờ.<br />Chi ph&iacute; : ph&iacute; thủ tục h&agrave;nh ch&aacute;nh .<br />Thủ  tục h&agrave;nh ch&aacute;nh tại ng&acirc;n h&agrave;ng gồm: ph&iacute; đảm bảo t&agrave;i sản, ph&iacute; mở t&agrave;i  khoản, ph&iacute; c&ocirc;ng chứng sao y, c&agrave; vẹt xe, mua bảo hiểm th&acirc;n xe 1,5% trị  gi&aacute; xe trong thời gian vay.<br />C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh<br />Mức t&agrave;i trợ tối đa 75% tr&ecirc;n t&agrave;i sản thế chấp.<br />Thời gian tối đa 05 năm.<br />L&atilde;i suất ưu đ&atilde;i : 1% - 1,1 th&aacute;ng.<br />Thời gian thẩm định hồ sơ sau khi nhận đầy đủ: 05 ng&agrave;y.<br />C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh đứng t&ecirc;n chủ xe, sau khi kh&aacute;ch trả hết nợ, c&ocirc;ng ty sẽ sang t&ecirc;n lại cho kh&aacute;ch.<br />Chi ph&iacute; : ph&iacute; thủ tục h&agrave;nh ch&iacute;nh<br />Thủ  tục h&agrave;nh ch&iacute;nh tại Ng&acirc;n h&agrave;ng gồm: ph&iacute; đảm bảo t&agrave;i sản, ph&iacute; mở t&agrave;i  khỏan, ph&iacute; c&ocirc;ng chứng sao y, c&agrave; vẹt xe, mua bảo hiểm th&acirc;n xe trong thời  gian vay.<br />Đơn xin vay vốn ng&acirc;n h&agrave;ng v&agrave; phương &aacute;n trả l&atilde;i (theo mẫu của c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh)</p>\r\n<p style="margin: 3.75pt 3.75pt 5pt 3pt; text-align: center;"><strong><span style="font-family: Arial; color: #f01b24; font-size: 10pt;">QUY TR&Igrave;NH MUA XE TRẢ G&Oacute;P</span></strong><span style="font-family: Times New Roman;"> </span></p>\r\n<p style="margin: 3.75pt 3.75pt 5pt 3pt;"><span style="font-family: Arial; font-size: 10pt;">Kh&aacute;ch h&agrave;ng chuẩn bị đầy đủ hồ sơ theo hướng dẫn của nh&acirc;n vi&ecirc;n t&iacute;n dụng.<br />Nh&acirc;n vi&ecirc;n thẩm định đến tận nh&agrave; để thẩm định v&agrave; lấy hồ sơ.<br />Sau khi c&oacute; giấy t&agrave;i trợ t&iacute;n dụng v&agrave; hồ sơ xe.<br />Kh&aacute;ch h&agrave;ng phải tiến h&agrave;nh đ&oacute;ng tiền xe v&agrave; chi ph&iacute; l&agrave;m thủ tục đăng k&yacute; xe.<br />Khi  c&oacute; biển số xe v&agrave; c&oacute; giấy hẹn, kh&aacute;ch h&agrave;ng l&ecirc;n ng&acirc;n h&agrave;ng k&yacute; hợp đồng t&iacute;n  dụng, đ&oacute;ng ph&iacute; h&agrave;nh ch&iacute;nh v&agrave; giấy nhận nợ của ng&acirc;n h&agrave;ng.<br />Khi  tiền chuyển khoản của ng&acirc;n h&agrave;ng v&agrave;o t&agrave;i khoản của BMW, th&igrave; kh&aacute;ch h&agrave;ng  mang theo CMND v&agrave; giấy giới thiệu l&ecirc;n nhận xe, k&yacute; bi&ecirc;n bản b&agrave;n giao xe  với giấy tờ xe hợp lệ theo ph&aacute;p luật.<br />Khi c&oacute; giấy đăng k&yacute; xe, Ng&acirc;n h&agrave;ng sẽ đi đăng k&yacute; v&agrave; sao y cho kh&aacute;ch h&agrave;ng một bản để sử dụng.<br />Trường  hợp kh&aacute;ch mua qua C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh th&igrave; kh&aacute;ch h&agrave;ng đ&oacute;ng tiền  xe tại C&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh. Chi ph&iacute; đăng k&yacute; xe kh&aacute;ch h&agrave;ng phải  chịu, Bi&ecirc;n bản b&agrave;n giao xe 03 b&ecirc;n c&ugrave;ng k&yacute; để c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh  giải ng&acirc;n cho h&atilde;ng xe.<br />Trường  hợp kh&aacute;ch h&agrave;ng ở tỉnh mua xe trả g&oacute;p th&igrave; chi ph&iacute; Dịch vụ đi đăng k&yacute; xe  do kh&aacute;ch h&agrave;ng chịu, t&ugrave;y theo tỉnh xa hay gần m&agrave; thỏa thuận.</span></p>\r\n', 'Bằng việc liên kết với nhiều ngân hàng lớn, BMW luôn sẵn sàng hỗ trợ khách hàng về vốn khi mua xe thông qua hình thức trả góp.', NULL, NULL, NULL, NULL, 0);

