INSERT INTO `product` (`id`, `product_name`, `product_description`, `product_option`, `product_details`, `product_engine`, `product_price`, `manufacturer`, `new_status`, `view`, `warranty`, `product_images`, `id_group_product`, `sold_out`, `status`, `product_cylinder`, `type_of_car`, `transmission`, `number_of_seats`) VALUES
(24, 'BMW 320i 2011 ', '\r\n<p>\r\n<object width="535" height="350" data="http://www.youtube.com/v/gT3WVpqBrMM" type="application/x-shockwave-flash">\r\n<param name="data" value="http://www.youtube.com/v/gT3WVpqBrMM" />\r\n<param name="src" value="http://www.youtube.com/v/gT3WVpqBrMM" />\r\n</object>\r\n&nbsp;</p>\r\n', '\r\n\r\n', '\r\n<p>Nội thất bọc da&nbsp;&nbsp;Sensatec</p>\r\n<p>Trang bị c&aacute;c hệ thống hỗ trợ an to&agrave;n điện tử</p>\r\n<p>Trang bị ch&igrave;a kho&aacute; v&agrave; n&uacute;t khởi động&nbsp;&nbsp;Start/Go</p>\r\n<p>Hộp số tự động 6 cấp</p>\r\n<p>Vỏ xe&nbsp;an to&agrave;n run-flat&nbsp;với&nbsp;chức năng&nbsp;cảnh b&aacute;o</p>\r\n<p>Ghế trước chỉnh điện từng phần</p>\r\n<p>6 t&uacute;i kh&iacute;</p>\r\n<p>CD 1 đĩa, MP3 , AUX/IN,</p>\r\n<p>Điều h&ograve;a kh&ocirc;ng kh&iacute; tự động với bộ vi lọc c&oacute; chức năng tận dụng nhiệt dư</p>\r\n<p>Chỉnh điện 1 phần cho ghế t&agrave;i xế v&agrave; h&agrave;nh kh&aacute;ch ph&iacute;a trước</p>\r\n<p>Hệ thống điều chỉnh radio Oceania</p>\r\n<p>Hệ thống đ&egrave;n Xenon chiếu gần v&agrave; chiếu xa</p>\r\n<p>Cảm biến mưa&nbsp;v&agrave;&nbsp;chức năng tự điều chỉnh đ&egrave;n l&aacute;i xe ban ng&agrave;y</p>\r\n<p>Bộ gương ngo&agrave;i gập điện</p>\r\n<p>Hệ thống kiểm so&aacute;t cự ly đỗ xe ph&iacute;a sau</p>\r\n<p>Ốp nội thất &aacute;nh bạc</p>\r\n<p>Chức năng tự kh&oacute;a c&aacute;c cửa khi xe chạy</p>\r\n<p>Khe c&agrave;i biển số</p>\r\n<p>Lớp chức năng bảo vệ bề mặt sơn xe</p>\r\n<p>Ph&acirc;n bổ trọng lượng tối ưu 50/50</p>\r\n<p><strong>Động cơ ph&ugrave; hợp với nhi&ecirc;n liệu c&oacute; chỉ số octane thấp</strong><strong>&nbsp;</strong></p>\r\n<p><strong>Phi&ecirc;n bản cho kh&iacute; hậu nhiệt đới</strong><strong>&nbsp;</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u nội thất</span></strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p>Đen hoặc beige</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u xe</span></strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p>Đen sapphire, Trắng Alpine, Bạc Titan, Xanh&nbsp;&nbsp;Montego,&nbsp;Đỏ Crimson , Đỏ Vermillion, X&aacute;m Space,<span style="font-size: x-small;">&nbsp;</span>Bluewate,&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;"><span style="font-size: x-small;">Option lựa chọn th&ecirc;m</span></span></strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p><span style="font-size: x-small;">Lazăng đ&uacute;c&nbsp;&nbsp;16 inh, 17 inh,18 inh</span></p>\r\n<p><span style="font-size: x-small;">Nẹp mạ cr&ocirc;m viền cửa xe</span></p>\r\n<p><span style="font-size: x-small;">M&agrave;n h&igrave;nh v&agrave; idrive,blueooth</span></p>\r\n<p><span style="font-size: x-small;">&nbsp;</span></p>\r\n<p><strong><span style="text-decoration: underline;"><span style="font-size: x-small;">Bảo h&agrave;nh</span></span></strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p>02 năm kh&ocirc;ng giới hạn km</p>\r\n', '\r\n<p><strong>Động cơ I4, 4 xi lanh thẳng h&agrave;ng</strong></p>\r\n<p><strong>Dung t&iacute;ch động cơ: 1.995 cc</strong></p>\r\n<p>C&ocirc;ng su&acirc;́t cực đại: 125 kw tại 6.700 v&ograve;ng ph&uacute;t</p>\r\n<p>M&ocirc;men xoắn cực đại: 210 Nm tại 4.250 v&ograve;ng ph&uacute;t</p>\r\n<p>Tự trọng: 1.475 kg</p>\r\n<p>D&agrave;i x Rộng x Cao: 4.531x2.013x1.421mm</p>\r\n<p>Chiều d&agrave;i cơ sở: 2.760 mm</p>\r\n<p>Ti&ecirc;u hao nhi&ecirc;n li&ecirc;̣u: 5.1- 8.9 l&iacute;t/ 100km (theo ti&ecirc;u chuẩn Ch&acirc;u &Acirc;u)&nbsp;</p>\r\n<p>Khả năng tăng tốc 1-100 km/h: 8,9 gi&acirc;y</p>\r\n', '1299000000', 'BMW', 1, NULL, 2, '433627012_20110429_2011_BMW_3_Series_Sedan_Front_Side_590x442.jpg', 1, 0, 1, '1.995 cc', 'Sedan', '6 số tự động', '4'),
(26, 'BMW 325i Cabriolet 3.0', '\r\n<p>\r\n<object style="width: 535px; height: 350px;" width="535" height="350" data="http://www.youtube.com/v/4-OpkEFB5Q8" type="application/x-shockwave-flash">\r\n<param name="data" value="http://www.youtube.com/v/4-OpkEFB5Q8" />\r\n<param name="src" value="http://www.youtube.com/v/4-OpkEFB5Q8" />\r\n</object>\r\n&nbsp;&nbsp;</p>\r\n', '\r\n\r\n', '\r\n<p><strong><span style="text-decoration: underline;">&nbsp;</span></strong></p>\r\n<p>Nội thất bọc da&nbsp;&nbsp;Sensatec</p>\r\n<p>M&acirc;m hợp kim nh&ocirc;m nhẹ, nan k&eacute;p kiểu spoke 360 - 16 inch</p>\r\n<p>Vỏ xe an to&agrave;n run-flat với chức năng cảnh b&aacute;o</p>\r\n<p>6 t&uacute;i kh&iacute;</p>\r\n<p>CD 1 đĩa, MP3 , AUX/IN,</p>\r\n<p>Nẹp mạ cr&ocirc;m viền cửa xe</p>\r\n<p>Tựa tay trung t&acirc;m ph&iacute;a sau</p>\r\n<p>Chỉnh điện 1 phần cho ghế t&agrave;i xế v&agrave; h&agrave;nh kh&aacute;ch ph&iacute;a trước</p>\r\n<p>Tay&nbsp;l&aacute;i bọc da kiểu thể thao</p>\r\n<p>Trang bị gạt t&agrave;n thuốc v&agrave; mồi lửa</p>\r\n<p>Hộp số tự động 6 cấp</p>\r\n<p>Bộ gương ngo&agrave;i gập điện</p>\r\n<p>Đ&egrave;n x&ecirc;-non chiếu gần v&agrave; chiếu xa</p>\r\n<p>Hệ thống cảnh b&aacute;o khoảng c&aacute;ch ph&iacute;a sau</p>\r\n<p>Cảm biến mưa v&agrave; tự điều chỉnh &aacute;nh s&aacute;ng đ&egrave;n</p>\r\n<p>Điều chỉnh nhiệt độ tự động 2 v&ugrave;ng</p>\r\n<p>Điều khiển s&oacute;ng &acirc;m thanh</p>\r\n<p>Phi&ecirc;n bản tiếng anh</p>\r\n<p><strong>Phi&ecirc;n bản cho kh&iacute; hậu nhiệt đới</strong></p>\r\n<p><strong>Th&iacute;&ecirc;t kế động cơ điều chỉnh chỉ số ốc tan thấp</strong><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u nội thất</span></strong></p>\r\n<p>Đen hoặc beige</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u xe</span></strong></p>\r\n<p>Đen sapphire, Trắng Alpine, Bạc Titan, Xanh&nbsp;&nbsp;Montego, Xanh Tasman,</p>\r\n<p>Đỏ Crimson , Đỏ Vermillion, X&aacute;m Space,<span style="font-family: Calibri;">&nbsp;</span>Bluewate,&nbsp;Deep&nbsp;Sea&nbsp;Blue</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Đặt th&ecirc;m d&agrave;nh cho kh&aacute;ch</span></strong></p>\r\n<p>Lazăng đ&uacute;c&nbsp;&nbsp;16 inh, 17 inh,18 inh</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Bảo h&agrave;nh</span></strong></p>\r\n<p>02 năm kh&ocirc;ng giới hạn km</p>\r\n', '\r\n<p><strong><span style="text-decoration: underline;">&nbsp;</span></strong></p>\r\n<p>Thiết kế động cơ: 6/4</p>\r\n<p>Dung t&iacute;ch : 2497 cc</p>\r\n<p>Thời gian tăng tốc từ 1-100km/h : 7.7 s</p>\r\n<p>Tốc độ tối đa : 242 km</p>\r\n<p>Ti&ecirc;u hao năng lượng : 8.8 l/km</p>\r\n<p>C&ocirc;ng&nbsp;suất cực đại : 160(210)/6500 kW(hp)/rpm</p>\r\n<p>M&ocirc;men xoắn cực đại : 250/2750 Nm/rpm</p>\r\n<p>Chiều d&agrave;i cơ sở : 2760 mm</p>\r\n<p>D&agrave;i x Rộng x Cao : 4531/ 1817 / 1421 mm</p>\r\n', '2455000000', 'BMW - 325i', 1, NULL, 2, '156389661_dgc1310545759.jpg', 1, 0, 1, '2996cc', 'Cabriolet', '6 số sàn', '4'),
(27, 'BMW 320i Cabriolet', '\r\n\r\n', '\r\n\r\n', '\r\n<p>M&acirc;m hợp kim nh&ocirc;m nhẹ, nan k&eacute;p kiểu Star spoke 157, 17"</p>\r\n<p>Bọc nệm da</p>\r\n<p>Ốp nh&ocirc;m &aacute;nh bạc</p>\r\n<p>Vỏ xe an to&agrave;n run-flat với chức năng cảnh b&aacute;o</p>\r\n<p>6 t&uacute;i kh&iacute;</p>\r\n<p>CD 1 đĩa, MP3 , AUX/IN,</p>\r\n<p>Ghế ngồi chỉnh điện với chế độ nhớ</p>\r\n<p>Tay&nbsp;l&aacute;i bọc da kiểu thể thao</p>\r\n<p>Trang bị gạt t&agrave;n thuốc v&agrave; mồi lửa</p>\r\n<p>Radio BMW chuy&ecirc;n biệt</p>\r\n<p>Đồng hồ đọc số km</p>\r\n<p>Hộp số tự động 6 c<span style="font-family: Calibri;">ấp</span></p>\r\n<p>Hệ thống cảnh b&aacute;o khoảng c&aacute;ch ph&iacute;a sau</p>\r\n<p>Bộ gương ngo&agrave;i gập được</p>\r\n<p>Đ&egrave;n x&ecirc;-non chiếu gần v&agrave; chiếu xa</p>\r\n<p>Hệ thống rửa đ&egrave;n pha tự động</p>\r\n<p>Cảm biến mưa v&agrave; hệ thống bật đ&egrave;n pha tự động</p>\r\n<p>Điều khiển s&oacute;ng &acirc;m thanh</p>\r\n<p>Phi&ecirc;n bản tiếng anh</p>\r\n<p>Chức năng điều chỉnh &aacute;nh s&aacute;ng đ&egrave;n ban ng&agrave;y</p>\r\n<p><strong>Thiết kế động cơ điều chỉnh chỉ số ốc tan</strong></p>\r\n<p><strong>Phi&ecirc;n bản cho kh&iacute; hậu nhiệt đới</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Kh&aacute;ch đặt th&ecirc;m</span></strong></p>\r\n<p>Cảnh b&aacute;o de trước v&agrave; sau</p>\r\n<p>Hệ thống điều h&ograve;a tự động</p>\r\n<p>Chức năng khởi động, đ&oacute;ng/mở cửa kh&ocirc;ng cần ch&igrave;a kh&oacute;a</p>\r\n<p>Chức năng tivi</p>\r\n<p>M&agrave;n h&igrave;nh tr&ecirc;n t&aacute;p l&ocirc;</p>\r\n<p>Chuẩn bị chức năng Bluetooth cho điện thoại</p>\r\n<p>Chức năng kết nối cổng USB</p>\r\n<p>Hệ thống &acirc;m thanh hifi</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u nội thất</span></strong></p>\r\n<p>Cream beige</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u xe</span></strong></p>\r\n<p><strong><span style="text-decoration: underline;">&nbsp;</span></strong>&nbsp;Trắng Alpine, Đỏ, Xanh&nbsp;&nbsp;Montego,</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Bảo h&agrave;nh</span></strong></p>\r\n<p>02 năm kh&ocirc;ng giới hạn km</p>\r\n', '\r\n<p><strong>Thiết kế động cơ thẳng hang In line/4/4</strong></p>\r\n<p><strong>Dung t&iacute;ch động cơ 1995 cc</strong></p>\r\n<p>C&ocirc;ng suất cực đại 115(156)/6400 kw(hp)/rpm</p>\r\n<p>M&ocirc;men xoắn cực đại 200/3600 Nm</p>\r\n<p>Thời gian tăng tốc từ 0 - 100k : 10.1 s</p>\r\n<p>Vận tốc tối đa : 216&nbsp;&nbsp;km/h</p>\r\n<p>Ti&ecirc;u hao nhi&ecirc;n liệu : 8.2 l/km</p>\r\n<p>Chiều d&agrave;i cơ sở 2760 mm</p>\r\n<p>K&iacute;ch thước (d&agrave;i x rộng x cao) : 4580 / 1782/ 1384 mm</p>\r\n', '2583000000', 'BMW 320i', 1, NULL, 2, '345444415_images.jpg', 1, 0, 1, '1995cc', 'Cabriolet', '6 số tự động', '5'),
(28, 'BMW 325i Cabriolet', '\r\n<p>\r\n<object width="535" height="350" data="http://www.youtube.com/v/98JaDyV81yk" type="application/x-shockwave-flash">\r\n<param name="data" value="http://www.youtube.com/v/98JaDyV81yk" />\r\n<param name="src" value="http://www.youtube.com/v/98JaDyV81yk" />\r\n</object>\r\n</p>\r\n', '\r\n\r\n', '\r\n<p>M&acirc;m hợp kim nh&ocirc;m nhẹ nan k&eacute;p kiểu 339 - 17 inch</p>\r\n<p>Nội thất bọc da cao cấp Dakota</p>\r\n<p>Vỏ xe an to&agrave;n run-flat với chức năng cảnh b&aacute;o</p>\r\n<p>6 t&uacute;i kh&iacute;</p>\r\n<p>CD 1 đĩa, MP3 , AUX/IN,</p>\r\n<p>Ốp nh&ocirc;m &aacute;nh bạc</p>\r\n<p>Gương ngo&agrave;i gập được</p>\r\n<p>Ghế trước chỉnh điện với chế độ nhớ</p>\r\n<p>Tay&nbsp;l&aacute;i bọc da thể thao</p>\r\n<p>Đồng hồ đọc số km</p>\r\n<p>Bộ tiện &iacute;ch</p>\r\n<p>- Gương trong xe tự điều chỉnh chống ch&oacute;i</p>\r\n<p>- Tựa tay ph&iacute;a trước trượt được</p>\r\n<p>- Chức năng khoang h&agrave;nh l&yacute;</p>\r\n<p>- Hệ thống kiểm so&aacute;t cự ly đỗ xe (PDC) ph&iacute;a sau</p>\r\n<p>- Cảm biến mưa</p>\r\n<p>- Hệ thống điều h&ograve;a tự động</p>\r\n<p>- Chức năng kiểm so&aacute;t h&agrave;nh tr&igrave;nh</p>\r\n<p>- Hệ thống đ&egrave;n</p>\r\n<p>- Hệ thống Radio BMW professional</p>\r\n<p>Hộp số tự động (6 số điện tử Steptronic)</p>\r\n<p>Hệ thống đ&egrave;n Xenon với chức năng chiếu gần &amp; chiếu xa</p>\r\n<p>Hệ thống rửa đ&egrave;n pha tự động</p>\r\n<p>Cổng kết nối USB</p>\r\n<p>Chuẩn bị chức năng Bluetooth cho điện thoại</p>\r\n<p><strong>Phi&ecirc;n bản cho kh&iacute; hậu nhiệt đới</strong></p>\r\n<p><strong>Thiết kế động cơ tự động điều chỉnh chỉ số octan</strong></p>\r\n<p>Tự động lock cửa khi xe chạy</p>\r\n<p>&nbsp;</p>\r\n<p><span style="text-decoration: underline;"><strong>Đặt th&ecirc;m (kh&aacute;ch đặt th&ecirc;m)</strong></span></p>\r\n<p>Hệ thống loa HiFi</p>\r\n<p>Ch&igrave;a kh&oacute;a th&ocirc;ng minh</p>\r\n<p>Ốp gỗ &oacute;c ch&oacute; m&agrave;u s&aacute;ng</p>\r\n<p>Chức năng tivi</p>\r\n<p>Hệ thống chờ cho đầu CD đa đĩa</p>\r\n<p>&nbsp;</p>\r\n<p><span style="text-decoration: underline;"><strong>M&agrave;u nội thất</strong></span></p>\r\n<p>Cream beige</p>\r\n<p><span style="text-decoration: underline;"><strong>M&agrave;u xe</strong></span></p>\r\n<p>Trắng Alpine, Đỏ, Xanh&nbsp;&nbsp;Montego,</p>\r\n<p>&nbsp;</p>\r\n<p><span style="text-decoration: underline;"><strong>Bảo h&agrave;nh</strong></span></p>\r\n<p>02 năm kh&ocirc;ng giới hạn km</p>\r\n', '\r\n<p>Thiết kế động cơ thẳng h&agrave;ng : In line/6/4</p>\r\n<p>Dung t&iacute;ch động cơ&nbsp;&nbsp;: 2497 cm3</p>\r\n<p>C&ocirc;ng suất cực đại : 160(218)/6500 kw(hp)/rpm</p>\r\n<p>M&ocirc;men xoắn cực đại : 250/2750 Nm</p>\r\n<p>Thời gian tăng tốc từ 0 - 100k :&nbsp;&nbsp;8.4 s</p>\r\n<p>Vận tốc tối đa : 245km/h</p>\r\n<p>Ti&ecirc;u hao nhi&ecirc;n liệu : 8.8l/km</p>\r\n<p>Chiều d&agrave;i cơ sở : 2760mm</p>\r\n<p>K&iacute;ch thước (d&agrave;i x rộng x cao) : 4580 / 1782/ 1384 mm</p>\r\n', '2937000000', 'BMW - 325i', 1, NULL, 2, '847915357_5.jpg', 1, 0, 1, '2497cc', 'Crossover', '6 số tự động', '4'),
(29, 'BMW 528i 2011', '\r\n<p>\r\n<object width="535" height="350" data="http://www.youtube.com/v/KEQdn57Kz1Q" type="application/x-shockwave-flash">\r\n<param name="data" value="http://www.youtube.com/v/KEQdn57Kz1Q" />\r\n<param name="src" value="http://www.youtube.com/v/KEQdn57Kz1Q" />\r\n</object>\r\n</p>\r\n', '\r\n\r\n', '\r\n<p><strong>Hệ thống loa HiFi 12 loa 1 ampli</strong></p>\r\n<p><strong>Cửa h&iacute;t</strong></p>\r\n<p><strong>R&egrave;m che nắng ph&iacute;a sau</strong></p>\r\n<p><strong>R&egrave;m che nắng b&ecirc;n h&ocirc;ng</strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>Nội thất bọc da Dakota</p>\r\n<p>Hộp số tự động 8 cấp</p>\r\n<p>M&acirc;m hợp kim nan h&igrave;nh chữ V kiểu 236, 17inch</p>\r\n<p>Lốp runflat</p>\r\n<p>Ốp nội thất v&acirc;n mờ</p>\r\n<p>8 t&uacute;i kh&iacute;</p>\r\n<p>CD 1 đĩa, MP3 , AUX/IN,</p>\r\n<p>Trang bị gạt t&agrave;n thuốc v&agrave; mồi lửa</p>\r\n<p>Ghế trước chỉnh điện với chế độ nhớ</p>\r\n<p>Đồng hồ đọc số km</p>\r\n<p>Chức năng đọc đĩa cho khu vực Ch&acirc;u &Aacute;</p>\r\n<p>Hệ thống phanh t&aacute;i sinh năng lượng</p>\r\n<p>Hệ thống kiểm so&aacute;t cự ly đỗ xe (PDC)</p>\r\n<p>Gương trong v&agrave; ngo&agrave;i xe tự điều chỉnh chống ch&oacute;i</p>\r\n<p>Gương chiếu hậu trong xe tự điều chỉnh chống ch&oacute;i</p>\r\n<p>Đ&egrave;n x&ecirc;-non chiếu gần v&agrave; chiếu xa</p>\r\n<p><strong>Phi&ecirc;n bản cho kh&iacute; hậu nhiệt đới</strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">M&agrave;u xe</span></strong></p>\r\n<p><strong>&nbsp;</strong>&nbsp;</p>\r\n<p>Trắng Alpine,&nbsp;X&aacute;m Space, Milano Beige, Havana,</p>\r\n<p>Deep Sea Blue</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">Bảo h&agrave;nh</span></strong></p>\r\n<p>02 năm kh&ocirc;ng giới hạn km</p>\r\n', '\r\n<p>Thiết kế động cơ ; In line/6/4</p>\r\n<p><strong>Dung t&iacute;ch động cơ ; 2996</strong></p>\r\n<p>C&ocirc;ng suất cực đại : 190(258)/6600 kw(hp)/rpm</p>\r\n<p>M&ocirc;men xoắn cực đại : 310/2600-5000 Nm</p>\r\n<p>Thời gian tăng tốc từ 0 - 100km : 6.7 s</p>\r\n<p>Vận tốc tối đa : 250 km/h</p>\r\n<p>Ti&ecirc;u hao nhi&ecirc;n liệu ; 7.6 l/km</p>\r\n<p>Chiều d&agrave;i cơ sở : 2968 mm</p>\r\n<p>K&iacute;ch thước (d&agrave;i x rộng x cao) : 4899 / 2094 / 1464 mm</p>\r\n', '2760000000', 'BMW', 1, NULL, 2, '741436810_guo1295841534.jpg', 2, 0, 1, '2996cc', 'Sedan', '8 số tự động', '5'),
(37, 'BMW 523i 2011', '\r\n<p>\r\n<object width="535" height="350" data="http://www.youtube.com/v/cOaQ9_O6NCI" type="application/x-shockwave-flash">\r\n<param name="data" value="http://www.youtube.com/v/cOaQ9_O6NCI" />\r\n<param name="src" value="http://www.youtube.com/v/cOaQ9_O6NCI" />\r\n</object>\r\n</p>\r\n', '\r\n<p><strong><span style="text-decoration: underline;">H&igrave;nh ảnh xe 5 series 2011</span></strong></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_2_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_19_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_9_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_14_cd_gallery-crop.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_22_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_13_cd_gallery-crop.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_20_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_16_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_1_cd_gallery.jpg" alt="" /></p>\r\n<p><img id="largeImage3" src="http://car4vn.com.vn/images/thuvienanh/2011_bmw_528i_3_cd_gallery.jpg" alt="" /></p>\r\n', '\r\n<p><strong><span style="text-decoration: underline;">&nbsp;</span></strong></p>\r\n<p><span style="font-size: x-small;">Nội thất bọc da Dakota</span></p>\r\n<p><span style="font-size: x-small;">Hộp số tự động 8 cấp</span></p>\r\n<p><span style="font-size: x-small;">M&acirc;m hợp kim nan h&igrave;nh chữ V kiểu 236, 17inch</span></p>\r\n<p><span style="font-size: x-s